\begin{id}vs20.kv.i3.n14\end{id}
\begin{source}VS\end{source}
\begin{eauthor}KV\end{eauthor}
\begin{etitle}Krajši del prežagane palice\end{etitle}
\begin{etext}
Palico dolžine $4$ m prežagamo na slučajnem mestu.
Naj bo $X$ dolžina krajšega kosa. Gostota $p_X(x)$ je enaka
\end{etext}
\begin{question}{radio}
\begin{ans}{F} $p_X(x)=x$ za $x\in\lbrack 0,2\rbrack$ in 0 sicer. \end{ans}
\begin{ans}{F} $p_X(x)=2x$ za $x\in\lbrack \frac{1}{2},1\rbrack$ in 0 sicer. \end{ans}
\begin{ans}{F} $p_X(x)=1$ za $x\in\lbrack 0, 2\rbrack$ in 0 sicer \end{ans}
\begin{ans}{F} $p_X(x)=0.5$ za $x\in\lbrack 2, 4\rbrack$ in 0 sicer. \end{ans}
\begin{ans}{F} $p_X(x)=2x-1$ za $x\in\lbrack 0, 2\rbrack$ in 0 sicer. \end{ans}
\begin{ans}{T} $p_X(x)=0.5$ za $x\in\lbrack 0, 2\rbrack$ in 0 sicer.\end{ans}
\begin{ans}{F} $p_X(x)=\frac{1}{4}$ za $x\in\lbrack 0, 4\rbrack$ in 0 sicer. \end{ans}
\begin{ans}{F} Nič od zgoraj naštetega. \end{ans}
\end{question}
