$$"},"lngs":["sl"],"breadcrumbs":{"document":{"title":null,"author":null},"chapter":"Izpiti pri VIS/OVS","section":"T/F","subsection":"VIS 2016: 2. rok"},"guid":"3390e21b5c633d2b6acd1fd8"}

