\begin{id}vs20.kv.i4.n174\end{id}
\begin{source}VS\end{source}
\begin{eauthor}KV\end{eauthor}
\begin{etext}
Naj bo $X\sim N(-1,2)$, $Y\sim N(1,3)$ in $E(XY)=2$.
\end{etext}
\begin{question}{numerical} Izračunaj koeficient korelacije $\rho(X,Y)$.
\begin{result}{0.01}
0.5
\end{result}
\begin{solution}
\begin{wide}
$$
\rho(X,Y)=\frac{\mbox{Cov}(X,Y)}{\sigma_X\sigma_Y}=\frac{E(XY)-E(X)E(Y)}{\sigma_X\sigma_Y}=
$$
$$
=\frac{2-(-1)\cdot 1}{2\cdot 3}=\frac{3}{6}=0.5.
$$
\end{wide}
\end{solution}
\end{question}
\begin{question}{numerical} Izračunaj disperzijo slučajne spremenljivke $Z=X+Y$.
\begin{result}{0.1}
19
\end{result}
\begin{solution}
\begin{wide}
$$
D(X+Y)=\sigma_X^2+2\rho\sigma_X\sigma_Y+\sigma_Y^2=
$$
$$
=2^2+2\cdot 0.5\cdot 2\cdot 3+3^2=19.
$$
\end{wide}
\end{solution}
