\begin{id}vs20.kv.i4.n172\end{id}
\begin{source}VS\end{source}
\begin{eauthor}KV\end{eauthor}
\begin{etext}
O slučajnih spremenljivkah $X$ in $Y$ vemo naslednje
$$2\mbox{Cov}(X,Y)-D(X)-D(Y)=0.$$
\end{etext}
\begin{question}{numerical} Izračunaj koeficient korelacije $\rho(X,Y)$.
\begin{result}{0.1}
1
\end{result}
\begin{solution}
Opazimo, da velja
\begin{wide}
$$
D(Y-X)=D(Y)+D(-X)+2\mbox{Cov}(Y,-X)=
$$
$$
=D(Y)+D(X)+2\mbox{Cov}(Y,-X)=
$$
$$
=D(X)+D(Y)-2\mbox{Cov}(X,Y)=0,
$$
\end{wide}
kar je izpolnjeno za $Y-X=c$, kjer je $c$ konstanta, oziroma
$Y=X+c$. Potem je $\rho(X,Y)=1$.\par
Do enakega zaključka pridemo, če bi začeli z $D(X-Y)$.
\end{solution}
\end{question}
