\begin{id}vs20.kv.i4.n169\end{id}
\begin{source}VS\end{source}
\begin{eauthor}KV\end{eauthor}
\begin{etext}
Naj bo slučajna spremenljivka $X$ enakomerno porazdeljena, $X\sim\mathcal{U}(0,1)$
in naj bo $Y=1-X$.
\end{etext}
\begin{question}{numerical} Izračunaj kovarianco Cov$(X,Y)$.
\begin{result}{0.001}
-0.083
\end{result}
\begin{solution}
Slučajni spremenljivki $X$ in $Y$ sta enakomerno porazdeljeni,
$X\sim Y\sim \mathcal{U}(0, 1)$.
Kovarianca je enaka
\begin{wide}
$$
\mbox{Cov}(X,Y)=\mbox{Cov}(X,1-X)=\mbox{Cov}(X,1)+\mbox{Cov}(X,-X)=
$$
$$
=0-\mbox{Cov}(X,X)=-D(X)=-\frac{1}{12}.
$$
\end{wide}
\end{solution}
\end{question}
