\begin{exercise}
\begin{id}ovs20.r1.i4.n05\end{id} 
\begin{source}VS\end{source}
\begin{eauthor}Aleksandra Franc in AJ\end{eauthor}
% \begin{etitle}Lubenice\end{etitle}
\begin{etext}
Lubenice tehtajo $11$, $15$ in $x$ kilogramov, 
pri čemer so vse tri teže različne 
in je $x$ med $11$ in $15$. 
Mediana njihovih teže je enaka njihovi povprečni teži. 
%
\end{etext}
\begin{question}{numerical}
Izračunaj vrednost $x$.
\begin{result}{0.5}
13
\end{result}
\begin{solution} 
Povprečje je enako $(26+x)/3$,
mediana pa $x$.
Od tod dobimo $x=13$.
\end{solution}
\end{question}
