\begin{exercise}
\begin{id}vs20.kv.i3.n30\end{id} 
\begin{source}VS\end{source}
\begin{eauthor}KV\end{eauthor}
%\begin{etitle}\end{etitle}
\begin{etext} 
Za slučajni vektor $(X,Y)$ naj bo 
$\{(x_i,y_i)\}_{i=1}^{81}$ njegov vzorec.
Na naslednji sliki so prikazani 
trije razsevni diagrami za $(X,Y)=(U_j,V_j)$ ($1\le j\le 3$).
% povezanosti med vrednostmi slučajnih spremenljivk $Y$ in $X$. 
\pic{0.48}{korgraf.png}
\end{etext}
\begin{question}{radio} 
Za katere podatke Pearsonov koeficient korelacije NI primerna
mera povezanosti?
\begin{ans}{F} Za podatke na $1.$ razsevnem diagramu.           \end{ans} % (a)
\begin{ans}{F} Za podatke na $2.$ razsevnem diagramu.           \end{ans} % (b)
\begin{ans}{T} Za podatke na $3.$ razsevnem diagramu.           \end{ans} % (c)
\begin{ans}{F} Za podatke na $1.$ in $2.$ razsevnem diagramu.   \end{ans} % (d)
\begin{ans}{F} Za podatke na $1.$ in $3.$ razsevnem diagramu     \end{ans} % (e)
\begin{ans}{F} Za podatke na $2.$ in $3.$ razsevnem diagramu     \end{ans} % (f)
\begin{ans}{F} Za podatke na vseh razsevnih diagramih.           \end{ans} % (g)
\begin{ans}{F} Nič od zgoraj naštetega.                           \end{ans} % (h)
\end{question}
\begin{question}{radio} Za katere podatke je Pearsonov koeficient korelacije 
strogo pozitiven?
\begin{ans}{T} Za podatke na $1.$ razsevnem diagramu.           \end{ans} % (a)
\begin{ans}{F} Za podatke na $2.$ razsevnem diagramu.           \end{ans} % (b)
\begin{ans}{F} Za podatke na $3.$ razsevnem diagramu.           \end{ans} % (c)
\begin{ans}{F} Za podatke na $1.$ in $2.$ razsevnem diagramu.   \end{ans} % (d)
\begin{ans}{F} Za podatke na $1.$ in $3.$ razsevnem diagramu     \end{ans} % (e)
\begin{ans}{F} Za podatke na $2.$ in $3.$ razsevnem diagramu     \end{ans} % (f)
\begin{ans}{F} Za podatke na vseh razsevnih diagramih.           \end{ans} % (g)
\begin{ans}{F} Nič od zgoraj naštetega.                           \end{ans} % (h)
\end{question}
\begin{question}{numerical} 
{\bf Bonus:}
Oceni Pearsonov koeficient korelacije za $(U_1,V_1)$.
\begin{result}{0.05}
1
\end{result}
\end{question}
\begin{question}{numerical} 
{\bf Bonus:}
Oceni Pearsonov koeficient korelacije za $(U_2,V_2)$.
\begin{result}{0.05}
-0.9
\end{result}
\end{question}
\begin{question}{numerical} 
{\bf Bonus$^2$:}
Oceni Pearsonov koeficient korelacije za $(U_3,V_3)$.
\begin{result}{0.05}
-0.9
\end{result}
\end{question}
\begin{question}{essay} 
{\bf Bonus$^*$:} Pojasni, kako si prišel do zgornje ocene.
(S točkami bodo nagrajeni samo odgovori, 
ki bodo podali ustrezno utemeljitev.)
\end{question}
\begin{question}{essay} 
{\bf Bonus$^*$:} Če si izbral pri pravem vprašanju, da 
Pearsonov koeficient korelacije ni primerna mera povezanosti
za nek par $(U_i,V_i)$, pojasni zakaj (in navedi za kateri $i$).
\end{question}
\begin{tags}T11,F01,D6,M11,P8,N1\end{tags}
