\begin{id}vs20.kv.i4.n61\end{id}
\begin{source}VS\end{source}
\begin{eauthor}Zbirka\end{eauthor}
\begin{etitle}Konstanta\end{etitle}
\begin{etext}
Slučajna spremenljivka $X$ ima diskretno porazdelitev
$P(X=k)=ck$ za $k=1,2,\ldots,10$.
\end{etext}
\begin{question}{numerical}
Izračunaj $c \in \mathbb{R}$.
\begin{result}{0.001}
0.018
\end{result}
\begin{solution}
Porazdelitvena shema slučajne spremenljivke $X$ je
\begin{wide}
$$
X\sim \left(\begin{smallmatrix}
1 &2 &3 &\ldots&10\cr
c &2c&3c&\ldots&10c
\end{smallmatrix}\right).
$$
\end{wide}
Potem je
$$
1=\sum_{k=1}^{10}c\cdot k=55c.
$$
Dobimo
$$
c=\frac{1}{55}=0.018.
$$
\end{solution}
\end{question}
\begin{tags}T04,F03,D3,M03,P8,N0,vajeOVS\end{tags}
\end{exercise}","metadata":{"year":null,"page":null},"text":{"sl":"Slučajna spremenljivka $X$ ima diskretno porazdelitev
