\begin{id}vs20.kv.i4.n161\end{id}
\begin{source}VS\end{source}
\begin{eauthor}KV\end{eauthor}
\begin{etext}
Naj bo slučajna spremenljivka $X$ porazdeljena eksponentno,
$X\sim\mathcal{E}\left(\frac{1}{2}\right)$.
\end{etext}
\begin{question}{numerical} Izračunaj $D(1-4X)$.
\begin{result}{0.001}
64
\end{result}
\begin{solution} Iz
$$
D(X)=\biggl(\frac{1}{2^2}\biggr)^{-1}=4
$$
sledi
$$
D(1-4X)=16D(X)=64,
$$
kjer smo pri prvem enačaju upoštevali, da ima za konstanto zamaknjena slučajna
spremenljivka nespremenjeno disperzijo in da lahko faktor izpostavimo,
če ga pri tem še kvadriramo.
\end{solution}
\end{question}
