\begin{id}vs20.kv.i4.n156\end{id}
\begin{source}VS\end{source}
\begin{eauthor}KV\end{eauthor}
\begin{etext}
Naj ima slučajna spremenljivka $X$ geometrijsko porazdelitev, $X\sim\mathcal{G}(p)$,
za $p\in (0,1)$.
\end{etext}
\begin{question}{numerical} Če je $D(X)=56$, potem je verjetnost $p$ enaka
\begin{result}{0.001}
0.125
\end{result}
\begin{solution}
Disperzija geometrijsko porazdeljene slučajne spremenljivke $X$ je enaka
$$
\frac{1-p}{p^2}=56.
$$
Rešimo dobljeno kvadratno enačbo, tj. $56p^2+p-1=0$ in vzamemo pozitivno rešitev $p=\frac{1}{8}$.
\end{solution}
\end{question}
