\begin{source}VS\end{source}
\begin{eauthor}KV\end{eauthor}
\begin{etext}
Naj bo slučajna spremenljivka $X$ porazdeljena s
$\chi^2(n)$, $n\in \mathbb{N}$.
\end{etext}
\begin{question}{numerical}
Izračunaj število prostostnih stopenj $n$,
če veš, da je $\sigma(2X)=4\sqrt{2}$.
\begin{result}{0.1}
4
\end{result}
\begin{solution} Iz
$$
D(X)=2n
$$
sledi
$$
4\sqrt{2}=\sigma(2X)=2\sigma(X)=2\sqrt{2n}
$$
oziroma $n=4$.
\end{solution}
\end{question}
