\begin{id}vs20.kv.i4.n163\end{id}
\begin{source}VS\end{source}
\begin{eauthor}KV\end{eauthor}
\begin{etext}
{\bf Bonus (točkovanje:
(a) $\pm 1$,
(b) $\pm 1$,
(c) $\pm 1$,
(d) $2$
(e) $8$).}
\par
Naj bo $(X,Y)$ normalno porazdeljen slučajni vektor
$\mathcal{N}(-1,3,2,0.5,0)$. Naj bo $Z=2X-Y$.
\end{etext}
\begin{question}{radio}
(a) Njuni robni porazdelitvi sta enaki njunima komponentama
in sta normalno porazdeljeni.
\begin{ans}{T} Drži. \end{ans}
\begin{ans}{F} Ne drži. \end{ans}
\end{question}
\begin{question}{radio}
(b) Slučajna spremenljivka $Z$ je normalno porazdeljena.
\begin{ans}{T} Drži. \end{ans}
\begin{ans}{F} Ne drži. \end{ans}
\end{question}
\begin{question}{radio}
(c) Slučajni spremenljivki $X$ in $Y$ nista neodvisni.
\begin{ans}{F} Drži. \end{ans}
\begin{ans}{T} Ne drži. \end{ans}
\end{question}
\begin{question}{numerical}
(d) Izračunaj pričakovano vrednost $E(Z)$
\begin{result}{0.1}
-5
\end{result}
\begin{solution}
Pričakovana vrednost linearne kombinacije je
enaka linarni kombinaciji posameznih pričakovanih vrednosti,
tj. $2(-1)-3=-5$.
\end{solution}
\end{question}
\begin{question}{numerical}
(e) Izračunaj disperzijo $D(Z)$
\begin{result}{0.01}
16.25
\end{result}
\begin{solution}
Za disperzijo zapišemo
\begin{wide}
$$
D(2X-Y)=4D(X)+D(Y)=4\,\cdot\, 2^2+0.5^2=16.25,
$$
\end{wide}
kjer smo uporabili t.i. Pitagorjev izrek, saj nekoreliranost
oz. neodvisnost slučajnih spremenljivk $X$ in $Y$ sledi iz $\rho=0$.
\end{solution}
\end{question}
